BEGIN
    EXECUTE IMMEDIATE 'DROP TABLE "user"';
EXCEPTION
    WHEN OTHERS THEN
        IF SQLCODE != -942 THEN
            RAISE;
        END IF;
END;
/

-- 테이블 생성
CREATE TABLE "user"
(
    "number" NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    "id" VARCHAR2(50) NOT NULL unique,
    "pw" VARCHAR2(255) NOT NULL,
    "name" VARCHAR2(100)
);